import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface courseResult {
    subject: string;
    code: string;
    title: string
    description: string;
}

export const Home = (): JSX.Element => {
    const [query, setQuery] = useState('');
    const [courseResult, setCourseResult] = useState<courseResult | undefined>();


    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setQuery(e.target.value);
      };

    const handleFormSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    axios
        .post('http://localhost:5000/api/search', { courseQuery: query })
        .then((response) => {
        setCourseResult(response.data.result);
        })
        .catch((error) => {
        console.error('Error:', error);
        });
    }; 

    return (
        <section className="bg-gray-900 text-white py-16">
            <div className="container mx-auto px-4">
                <h1 className="text-4xl font-bold mb-4">Course Search</h1>
                <p className="text-lg mb-8">Enter a query for courses:</p>
                <form onSubmit={handleFormSubmit}>
                    <input 
                    type='text'
                    placeholder='ex. I want to learn about modern arabic literature'
                    value={query}
                    onChange={handleInputChange}
                    />
                    <button 
                    type='submit' 
                    className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-md">
                    Generate courses
                    </button>
                </form>
                {courseResult && (
                <div>
                    <h2>{courseResult.title}</h2>
                    <p>{courseResult.description}</p>
                </div>
                )}
            </div>
        </section>
    )
}
