import { SetStateAction, useState } from "react";

export default function Footer() {

  const [email, setEmail] = useState('');

  const handleInputChange = (e: { target: { value: SetStateAction<string>; }; }) => {
    setEmail(e.target.value);
  };

  const handleSubmit = (e: { preventDefault: () => void; }) => {
    e.preventDefault();
    //send email to server
    console.log('Email:', email);
    setEmail('');
  };

  return (
    <footer className='footer-container py-16 px-64 bg-slate-600 text-white font-oswald'>
        <div className='flex flex-row items-center justify-between'>
            <div className='logo-container flex flex-col items-center justify-center'>
              <img src="/images/image-logo-small.jpg" className="h-16" alt="Logo" />
              <h1 className="text-center">UPENN APAMSA</h1>
            </div>
            <div className='footer-newsletter'>
                <h1 className='font-semibold text-base text-center'>SUBSCRIBE TO OUR NEWSLETTER!</h1>
                <div className='subscribe-button'>
                  <form onSubmit={handleSubmit}>
                    <input
                      type='email'
                      value={email}
                      onChange={handleInputChange}
                      placeholder='yourname@upenn.edu'
                      className='border border-gray-300 px-2 py-1 mr-2'
                    />
                    <button
                      type='submit'
                      className='bg-slate-500 text-white px-4 py-2 transition duration-200 hover:bg-slate-200 hover:text-slate-800'
                    >
                      SUBMIT
                    </button>
                  </form>
                </div>
            </div>
            <div className='footer-links'>
                <h1 className='text-sm font-semibold'>OUR LINKS</h1>
            </div>
        </div>
    </footer>
  );
}
