import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface CourseResult {
  subject: string;
  code: string;
  title: string;
  description: string;
}

export const Home = (): JSX.Element => {
  const [query, setQuery] = useState('');
  const [courseResults, setCourseResults] = useState<CourseResult[]>([]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setQuery(e.target.value);
  };

  const handleFormSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    axios
      .post('http://localhost:5000/api/search', { courseQuery: query })
      .then((response) => {
        setCourseResults(response.data.result);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <section className="bg-white text-blue-900 min-h-screen w-screen font-sans">
      <div className="container flex flex-col items-center justify-center mx-auto h-full overflow-auto py-16">
        <h1 className="text-4xl font-bold mb-4">Course Search</h1>
        <p className="text-lg mb-8">Enter a query for courses:</p>
        <form onSubmit={handleFormSubmit} className="flex flex-col items-center mb-4">
          <div className="mb-4">
            <input
              type="text"
              placeholder="ex. arabic literature"
              value={query}
              onChange={handleInputChange}
              className="px-2 py-3 border border-blue-900 rounded-md focus:outline-none
               focus:ring-2 focus:ring-blue-700 text-blue-800 h-20 resize-none"
              name="courseQuery"
            />
          </div>
          <button
            type="submit"
            className="bg-blue-900 hover:bg-blue-700 text-white px-6 py-3 rounded-md transition-colors duration-300"
          >
            Generate courses
          </button>
        </form>
        {courseResults.length > 0 && (
          <div>
            {courseResults.map((courseResult, index) => (
              <div key={index} >
                <h2>{courseResult.subject} {courseResult.code} - {courseResult.title}</h2>
                <p>{courseResult.description}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
};
