import React from "react"
import { Formik, Form, Field, ErrorMessage } from "formik"
import * as Yup from "yup"
import axios from "axios"
import { useNavigate } from "react-router-dom"


export function CreatePost() {

    interface Announcement {
        title: string;
        body: string;
        date: string;
      }

    let navigate = useNavigate()

    const initialValues : Announcement = {
        title: "",
        body: "",
        date: "",
    }

    const onSubmitA = (data: Announcement) => {
        axios.post("http://localhost:3001/announcements", data).then((response) => {
          navigate("/");
        });
      };


    const validationSchemaA = Yup.object().shape({
        title: Yup.string().required(),
        body: Yup.string().required(),
        date: Yup.string().min(3).max(15).required()
    })

    return (
        <div className="create-post-page h-full w-screen flex flex-col items-center justify-center font-oswald">
            <div className='create-header w-full bg-slate-700'>
                <div className='exec-text-container mt-36 mb-4 ml-32 font-oswald'>
                    <h1 className='text-6xl text-white'>
                        CREATE A POST
                    </h1>
                </div>
            </div>
            <div className="create-container">
                <Formik initialValues = {initialValues} onSubmit = {onSubmitA} validationSchema = {validationSchemaA}>
                    <Form className="formContainer flex flex-col my-16 text-center">
                        <label> Title: </label>
                        <ErrorMessage name = "title" component="span"/>
                        <Field id="inputCreatePost" name="title" placeholder="ex...Title" />

                        <label> Body: </label>
                        <ErrorMessage name = "body" component="span"/>
                        <Field id="inputCreatePost" name="body" placeholder="ex...Post" />

                        <label> Date: </label>
                        <ErrorMessage name = "date" component="span"/>
                        <Field id="inputCreatePost" name="date" placeholder="ex...4/12/2003" />
                        <button type = "submit" className="mt-4 px-4 py-2 text-white bg-slate-800 font-semibold hover:bg-opacity-75 transition duration-200"> Create a Post </button>
                    </Form>
                </Formik>
            </div>
        </div>
    )

}
